<?php
class weixinqy_indexClassModel extends weixinqyModel { public function getagentid($names) { $agesta = explode(',', $names); $name = $agesta[0]; $sid = -1; $where = "`name`='$name'"; if(is_numeric($name))$where='`agentid`='.$name.''; $nsrs = $this->db->getone('[Q]wxqy_agent', $where, 'agentid'); if($nsrs){ $sid = (int)$nsrs['agentid']; } if($sid==-1 && count($agesta)>1){ $sid = $this->getagentid(substr($names, strpos($names, ',')+1)); } return $sid; } private function sendmsg($touid, $agentname, $body) { $agentid= $this->getagentid($agentname); $msg = ''; $barr = array('errcode' => -1,'msg' => 'sorry!error'); if($agentid==-1){ $msg= 'not found agent['.$agentname.']'; $barr['msg'] = $msg; return $barr; } $touser = ''; if($touid=='all'){ $touser = '@all'; }else{ $uarrs = $this->db->getall("select a.`user` from `[Q]admin` a left join `[Q]wxqy_user` b on a.`user`=b.`userid` where a.`status`=1 and b.id is not null and (b.`avatar` is not null or b.`status`=1) and a.`id` in($touid)"); foreach($uarrs as $k=>$rs){ $touser.='|'.$rs['user'].''; } if($touser!='')$touser = substr($touser, 1); } if($touser==''){ $msg= 'not found touser'; $barr['msg'] = $msg; return $barr; } $body = str_replace(array('{touser}', '{agentid}', "\n"), array($touser, $agentid, '<br>'), $body); if(getconfig('asynsend')){ $ybbo = m('reim')->asynurl('asynrun','wxqysendmsg', array( 'body' => $this->rock->jm->base64encode($body), 'agentid' => $agentid )); $barr['errcode'] = 0; $barr['msg'] = '异步发送'; if($ybbo)return $barr; } $barr = $this->sendbody($body, $agentid); return $barr; } public function sendbody($body, $agentid) { $barr = array('errcode' => -1,'msg' => 'sorry!error'); $token = $this->gettoken($agentid, true); $url = ''.$this->gettourl('URL_messagesend').'?access_token='.$token.''; $result = c('curl')->postcurl($url, $body); if($result!=''){ $arr = json_decode($result); $barr['errcode'] = $arr->errcode; $barr['msg'] = $arr->errmsg; } return $barr; } public function sendtext($touid, $agentname, $cont) { $body = '{"touser": "{touser}","msgtype": "text","agentid":{agentid},"text": {"content": "'.$cont.'"}}'; return $this->sendmsg($touid, $agentname, $body); } public function sendnews($touid, $agentname, $cont0=array(), $cont1=false, $cont2=false) { $s1 = ''; foreach($cont0 as $k=>$v){ if($k=='url')$v.= (contain($v,'?') ? '&' : '?').'agentid={agentid}'; $s1.=',"'.$k.'":"'.$v.'"'; } if($s1!='')$s1 = substr($s1,1); $articles = '[{'.$s1.'}]'; $body = '{"touser": "{touser}","msgtype": "news","agentid":{agentid},"news":{"articles": '.$articles.'}}'; $barr = $this->sendmsg($touid, $agentname, $body); return $barr; } public function sendtextcard($touid, $agentname, $cont0=array()) { $s1 = ''; foreach($cont0 as $k=>$v){ if($k=='url')$v.= (contain($v,'?') ? '&' : '?').'agentid={agentid}'; $s1.=',"'.$k.'":"'.$v.'"'; } if($s1!='')$s1 = substr($s1,1); $articles = '{'.$s1.'}'; $body = '{"touser": "{touser}","msgtype": "textcard","agentid":{agentid},"textcard":'.$articles.'}'; $barr = $this->sendmsg($touid, $agentname, $body); return $barr; } public function sendxiao($touid, $agentname, $wxarr=array()) { $barr = $this->backarr; if(isset($wxarr['picurl'])){ $barr = $this->sendnews($touid, $agentname, $wxarr); }elseif($wxarr['url'] != ''){ $barr = $this->sendtextcard($touid, $agentname, $wxarr); }else{ $cont = $wxarr['title'].'<br>'.$wxarr['description']; $barr = $this->sendtext($touid, $agentname, $cont); } return $barr; } }